<?phpclass TK_WP_Form_Button extends TK_Form_Button{	/**	 * PHP 4 constructor	 *	 * @package Themekraft Framework	 * @since 0.1.0	 * 	 * @param array $args Array of [ $id Id, $name Name, $value Value, $submit use submit, $extra Extra checkbox code   ]	 */	function tk_wp_form_button( $value, $args = array() ){		$this->__construct( $value, $args );	}		/**	 * PHP 5 constructor	 *	 * @package Themekraft Framework	 * @since 0.1.0	 * 	 * @param array $args Array of [ $id Id, $name Name, $value Value, $submit use submit, $extra Extra checkbox code   ]	 */	function __construct( $value, $args = array() ){		global $tk_form_instance_option_group;				$field_name = $tk_form_instance_option_group . '_values[' . $args['name'] . ']';			$args['name'] = $field_name;				parent::__construct( $value, $args );	}}function tk_form_button( $value, $args = array(), $return_object = FALSE ){	$button = new TK_WP_Form_Button( $value, $args );		tk_add_text_string( $value );		if( TRUE == $return_object ){		return $button;	}else{		return $button->get_html();	}}